mapper.select:
select * from member 

	where me_gr_name=#{cri.search} or me_name=#{cri.search}


	where me_gr_name=#{cri.search}


	where me_name=#{cri.search}

ORDER BY me_date DESC
LIMIT #{cri.pageStart}, #{cri.perPageNum}

select count(*) from member 	

	where me_gr_name=#{cri.search} or me_name=#{cri.search}


	where me_gr_name=#{cri.search}


	where me_name=#{cri.search}
