mapper.select:
SELECT *, bo_name AS po_bo_name, me_name AS po_me_name FROM post JOIN board ON bo_num = po_bo_num JOIN member ON me_id = po_me_id
where po_title like concat('%', #{cri.search},'%') or po_me_id = #{cri.search}
where po_title like concat('%', #{cri.search},'%')
where po_me_id = #{cri.search}
and po_bo_num = #{bo_num} ORDER BY po_num DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT count(*) FROM post JOIN member ON me_id = po_me_id
where po_title like concat('%', #{cri.search},'%') or po_me_id = #{cri.search}
where po_title like concat('%', #{cri.search},'%')
where po_me_id = #{cri.search}

select * from comment where cm_po_num = #{cri.poNum}
order by cm_num desc
limit #{cri.pageStart}, #{cri.perPageNum}

mapper.update:
update comment
set
	cm_content = #{co.cm_content}
where
	cm_num = #{co.cm_num}
